exports.endResonse = function (error, message, res, result) {
    if (!result) {
        result = {};
    }
    result.error = error;
    result.msg = message;
    result.lt = new Date().getTime();
    res.end(JSON.stringify(result));
};

exports.returnjsondata = function (res, result) {
    res.end(JSON.stringify(result));
};